import React, { useEffect, useState } from 'react'
import Navbar from '../components/Navbar'
import Footer from '../components/Footer'
import Card from '../components/Card';
import Carousel from '../components/Carousel';

export default function Home() {
    const [data, setData] = useState([])
    const [dataCategory, setDataCategory] = useState([])

    useEffect(() => {
        getData()
    }, [])

    const getData = async () => {
        const fetchdata = await fetch('http://localhost:5000/foodItem', {
            method: "get",
            headers: {
                'Content-Type': 'application/json'
            }
        })
        const result = await fetchdata.json()
        setData(result[0])
        setDataCategory(result[1])
        console.log(111, data)
        console.log(222, dataCategory)
    }


    return (
        <div>
            <div><Navbar /></div>
            <div><Carousel /></div>
            {dataCategory.map((item) => (<div>
                <h1>{item.CategoryName}</h1>
                {data.map((innerItem) => {
                    if (innerItem.CategoryName === item.CategoryName) {
                       return <div className='m-3'><Card cardData={innerItem}/></div>
                    }
                })}
            </div>
            ))}
            <div><Footer /></div>
        </div>
    );

}
